import React from 'react';
import 'antd/dist/antd.css';
// import Header from './Header';
import MetodosAxios from '../../requerimientos/MetodosAxios';
import { Typography, Card, Rate, Descriptions } from 'antd';

const { Title } = Typography;

export default class LibrosCalificados extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      libros: [],
      libros_calificados: [],
    }
  }

  obtener_libros = () => {
    let libros = [];
    MetodosAxios.obtener_libros().then(res => {
      // console.log(res)
      res.data.map(registro => {
        let libro = {
          titulo: registro.titulo,
          isbn: registro.isbn,
          rating: 0,
        };
        libros.push(libro);
      });
      // console.log('libros', libros);
      this.setState({
        libros: libros,
      }, () => {
        console.log('this.state.libros', this.state.libros);
        this.obtener_libros_calificados();
      });
    });
  }

  obtener_libros_calificados = () => {
    let libros_calificados = [];
    MetodosAxios.obtener_libros_calificados().then(calificaciones => {
      calificaciones.data.map(libro_calificado => {
        this.state.libros.map(libro => {
          // console.log('libro.isbn, libro_calificado.isbn', libro.isbn, libro_calificado.isbn);
          if (libro.isbn === libro_calificado.isbn) {
            // console.log('libros iguales');
            libro.key = libro.isbn;
            console.log(libro);
            libros_calificados.push(libro);
          }
        });
      });
      /**
         * Aqui terminan los dos lazos
         */
      console.log('libros_calificados', libros_calificados);
      // this.setState({
      //   libros_calificados: libros_calificados,
      // }, () => {
      //   console.log('this.state.libros_calificados', this.state.libros_calificados);
      // });
    });
  }

  componentDidMount = () => {
    this.obtener_libros();
    // this.obtener_libros_calificados();
  }

  render() {
    return (
      <div>
        {/* <Header /> */}
        <Title>Libros Calificados</Title>
        <Card title="Libros Calificados">
          <Card
            style={{ marginTop: 16 }}
            type="inner"
            title="Libro 1"
            extra={<Rate disabled defaultValue={2} />}
          >
            <Descriptions
              bordered
              column={{ xxl: 4, xl: 3, lg: 3, md: 3, sm: 2, xs: 1 }}
              size="small"
              layout="vertical"
            >
              <Descriptions.Item label="Titulo">Titulo 1</Descriptions.Item>
              <Descriptions.Item label="ISBN">ISBN 1</Descriptions.Item>
              <Descriptions.Item label="Descripcion" span={12}>Descripcion 1</Descriptions.Item>
            </Descriptions>
          </Card>
        </Card>
      </div>
    );
  }
}
