import React from 'react';
import {
  Table,
  Form,
  Input,
  InputNumber,
  DatePicker,
  Button,
  message,
  Rate,
  Dropdown,
  Menu,
  Icon,
  Popconfirm,
  Divider,
} from "antd";
const FormItem = Form.Item;
const { SubMenu } = Menu;

function handleMenuClick(e) {
  console.log('click', e);
}

const menu = (
  <Menu onClick={handleMenuClick}>
    <Menu.Item key="1">Editar</Menu.Item>
    <Menu.Item key="2">Eliminar</Menu.Item>
  </Menu>
);

class EditableTable extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      libros: [],
      editingKey: "",
      loading: true,
      rating: -1,
    }
    this.handleChangeRating = this.handleChangeRating.bind(this);
  }

  handleChangeRating = rating => {
    this.setState({
      rating: rating,
    }, () => {
      console.log('this.state.rating', this.state.rating);
    });
  }

  generar_libros = () => {
    let libros = [];
    let cont = 0;
    let id_autor = 0;
    for (let i = 0; i < 100; i++) {
      cont += 1;

      if (cont == 20) {
        cont = 0;
        id_autor += 1;
      }
      let libro = {
        id: i,
        // el campo key sirve para que sea unico en la tabla        
        key: i,
        id_autor: id_autor,
        titulo: `Libro ${i}`,
        isbn: `1111111111`,
        descripcion: `test-descripcion`,
        rating: 5,
      };
      libros.push(libro);
    }
    this.setState({
      libros: libros,
    }, () => {
      console.log('this.state.libros', this.state.libros);
      this.obtener_libros_del_autor(parseInt(this.props.autor_seleccionado.id));
    });
  }

  obtener_libros_del_autor = (id_autor_seleccionado) => {
    let libros_del_autor = [];
    this.state.libros.map(libro => {
      if (libro.id_autor === id_autor_seleccionado) {
        libro.isEditing = false;
        libros_del_autor.push(libro);
      }
    });
    this.setState({
      data: libros_del_autor,
      loading: false,
    }, () => {
      console.log('this.state.data', this.state.data);
    })
  }

  componentDidMount = () => {
    this.generar_libros();
  }

  isEditing = record => {
    return record.key === this.state.editingKey;
  };

  delete(key) {
    let rows = this.state.data;
    // console.log('rows', rows);
    console.log('this.state.data', this.state.data);
    for (let i = 0; i < rows.length; i++) {
      if (rows[i].key === key)
        rows.splice(i, 1)
    }
    // console.log('rows', rows);
    this.setState({
      data: rows,
    }, () => {
      console.log('this.state.data', this.state.data);
    })
  }

  edit(key) {
    let rows = [];
    this.state.data.map(obj => {
      if (obj.key === key)
        obj.isEditing = true
      else
        obj.isEditing = false
      rows.push(obj)
    })
    this.setState({ editingKey: key, data: rows });
  }


  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFields((err, row) => {
      if (err)
        return
      console.log('row', row);

      let item = undefined;
      this.state.data.map(registro => {
        if (registro.key === this.state.editingKey) {
          item = registro;
        }
      });
      console.log('item', item);

      let rows = this.state.data;
      rows.map( obj => {
        console.log('obj.key, item.key', obj.key, item.key);
        if ( obj.key === item.key ) {
          /**
           * Edito unicamente el rating 
           */
          obj.rating = row.rating;
          console.log('ENVIO FORMULARIO', obj);
          this.setState({
            data: rows,
          }, () => {
            console.log('this.state.data', this.state.data);
          });
        }
      });


    })

    // INICIO cerrar la edicion
    let rows = [];
    this.state.data.map(obj => {
      obj.isEditing = false
      rows.push(obj)
    })
    this.setState({ editingKey: "", data: rows });
    // FIN cerrar la edicion
  }

  cancel = () => {
    let rows = [];
    this.state.data.map(obj => {
      obj.isEditing = false
      rows.push(obj)
    })
    this.setState({ editingKey: "", data: rows });
  };

  render() {
    const { getFieldDecorator } = this.props.form;
    return (
      <Form onSubmit={this.handleSubmit}>
        <Table
          dataSource={this.state.data}
          columns={[
            {
              title: 'Titulo',
              dataIndex: 'titulo',
              editable: false,
              render: (text, record) => {
                return <span>{text}</span>
              }
            },
            {
              title: 'ISBN',
              dataIndex: 'isbn',
              key: 'isbn',
              editable: false,
              render: (text, record) => {
                return <span>{text}</span>
              }
            },
            {
              title: 'Descripcion',
              dataIndex: 'descripcion',
              key: 'descripcion',
              editable: false,
              render: (text, record) => {
                return <span>{text}</span>
              }
            },
            {
              title: 'Rating',
              key: 'rating',
              dataIndex: 'rating',
              editable: false,
              render: (text, record) => {
                if (record.isEditing) {
                  return <FormItem>
                    {getFieldDecorator('rating', {
                      rules: [
                        {
                          required: true,
                          message: `Por favor ingrese el rating!`
                        }
                      ],
                      initialValue: record.rating
                    })(
                      <Rate />
                    )}
                  </FormItem>
                } else
                  return <Rate disabled defaultValue={record.rating} />
              }
            },
            {
              title: 'Action',
              key: 'action',
              render: (text, record) => {
                return (
                  <div>
                    {
                      record.isEditing == false &&
                      <span>
                        <a onClick={() => this.edit(record.key)}>Editar</a>
                        <Divider type="vertical" />
                        <a onClick={() => this.delete(record.key)}>Eliminar</a>
                      </span>
                    }
                    {
                      record.isEditing &&
                      <span>
                        <Button htmlType="submit">Aceptar</Button>
                        {/*<Popconfirm title="¿Seguro de Aceptar?" onConfirm={() => this.handleSubmit(record)}>
                          <a>Aceptar</a>
                        </Popconfirm>*/}
                        <Divider type="vertical" />
                        <Popconfirm title="¿Seguro de Cancelar?" onConfirm={() => this.cancel(record.key)}>
                          <a>Cancelar</a>
                        </Popconfirm>
                      </span>
                    }
                  </div>
                )
              }
            },
          ]}
          bordered
          loading={this.state.loading}
          rowClassName="editable-row"
          title={() => `Autor:  ${this.props.autor_seleccionado.nombre}`}
          footer={() => `Número de libros encontrados: ${this.state.data.length}`}
        // showHeader
        />
      </Form>
    )
  }
}

const WrappedEditableTable = Form.create({})(EditableTable);
export default WrappedEditableTable;
